var obj = result;
var number =obj.length;
var nb=0;
var liarr = [];
var li={};
for(var i=0;i<number;i++)
{
	title=obj[i].title;
	url="/novel/app/novel/chapter?id="+obj[i].id+"&viewId=16755023882598961";
	type=obj[i].type;
	java.log(type);
	if(type==1){
		li.title=title;
		li.url=url;
		liarr[nb]=JSON.stringify(li);
	  nb++;    
		}

	if(obj[i].children.length>0){
		//java.log(obj[i].children.length);
		for(var x =0;x<obj[i].children.length;x++){
				li.title=title+"--"+obj[i].children[x].title;
	     //java.log(li.title);
	     li.url="/novel/app/novel/chapter?id="+obj[i].children[x].id+"&viewId=16755023882598961";
	     liarr[nb]=JSON.stringify(li);
	     nb++;    
			}
		}
