bookSourceComment:
/*自动获取搜索地址，以下勿动！❗️⚠️
legado by:youke2580 */
//获取网址和变量
var u=source.key,
v=source.getVariable();
//如果获取失败用webview动态加载获取
function web(){
	var w=java.webView(null,u,null);
	var s=w.match(/el-tag.*window.open.'(.+?)','/)[1]
	h=/^https?/.test(s);
	//网址如果不完整，二次加载
	if(h){
		s=s;
		}else{
			we=java.webView(null,u+s,null);
			s=we.match(/el-tag.*window.open.'(.+?)','/)[1];
			}
	return s;
	}
//获取搜索地址
function get(){
		var x=java.ajax(u),
	p=org.jsoup.Jsoup.parse(x),
j=p.select("head>script").get(1).attr('src');
	var html=java.ajax(u+j),
	a=html.match(/action="(.+?)"/);
	var so=a?a[1]:web();
	source.setVariable(so);
	return so;
	}
var url;
if(!v||v==""){
	url=get();
	}else{
		try{
		java.head(v,{})
		}catch(err){
			java.log(err)
			java.toast("搜索无效，重新获取！")
			v=get()
		}finally{v}
		url=v}
url;

