@js:
sort=[];
push=(title,url,type)=>{
		return sort.push(JSON.stringify({
				title: title,
				url: url?url:"",
				style: {
						layout_flexGrow: 1,
						layout_flexBasisPercent: type
					}
			}))
	}
$$=(categoryid,classid,end,rank)=>`http://api.xiaoshuoyun.cn/appapi/sc/2.7.0.016/mfxschuawei/commonpage/listtype%253Dv5.getbookclassdata%2526categoryid%253D${categoryid}%2526classid%253D${classid}%2526word%253D0%2526end%253D${end}%2526rank%253D${rank}%2526pageindex%253D{{page}\}%2526pagesize%253D10`;
[
		["男生",[["都市小说",8],["玄幻奇幻",7],["军事历史",9],["武侠小说",10]]],
		["女生",[["言情小说",3],["青春校园",4],["宫斗小说",5],["总裁小说",6],["穿越小说",11]]],
		["悬疑",[["灵异推理",1],["恐怖小说",2]]]
].map(([title,list],categoryid)=>{
		categoryid++
		push("༺ˇ»`ʚ "+title+" ɞ´«ˇ༻",$$(categoryid,0,0,"renqi"),1);
		list.map([title,classid]=>{
				push("༺ "+title+" ༻",$$(categoryid,classid,0,"renqi"),1);
				[["人气","renqi"],["完本","end"],["畅销","buy"],["新书","newbook"],["收藏","collect"]].map([title,rank]=>{
						["["+title+"]","连载","完结"].map((title,end)=>{
								push(title,$$(categoryid,classid,end,rank),0.25);
							});
					});
			});
	});
"["+sort.toString()+"]"

