ruleContent.content:
@js:
imgl=eval(result.match(/var chapterImages = (\[[\s\S]+?\])/)[1]);
host=result.match(/var chapterImageHost = "([^"]*)";/)[1].replace(/\\/g,'');
html='';
function get7ImageUrl(image) {
if (image.match(/^(http?)/i)) {
        return image
    } else if (image.match(/^(\/|images?)/i)) {
        return host + image
    }
}
for(i in imgl){
if(host){
url=get7ImageUrl(imgl[i]);
}else{
url=host+imgl[i]
}
html+='<img src="'+url+'">\n'
}
html

