ruleContent.content:
@js:Cookie={}
args={}
baseUrl.match(/[^?=&]+=[^&]*/g).map(v=>{v=v.match(/(.+?)=(.*)/);if(v)args[v[1]]=v[2]})
curl=source.key+'/truyen/'+args.h+'/1/'+args.bookid+'/'+args.c+'/'
window={setInterval:function(){},addEventListener:function(a,b,c){},getCookie:function(name){return Cookie[name]}}
function split(s){
	s.split(';').map(v=>{v=v.match(/(.+?)=(.*)/);if(v)Cookie[v[1]]=v[2]})
}
function concat(o){
	return Object.entries(o).map(v=>v.join('=')).join(';')
}
function updateCookie(){
	r=java.get(curl,{"Cookie":concat(Cookie)})
	_gac=r.body().match('_gac=.+?(?=;)')
	_gac&&split(_gac[0])
	_ac=r.body().match('_ac=.+?(?=;)')
	_ac&&split(_ac[0])
	split(concat(r.cookies()))
	!Cookie._ac&&updateCookie()
	String(r.body()).replace(/var ([\w\d]+?)\s*=\s*['"](.+?)['"]/g,function(a,name,value){this[name]=value})
	eval(String(source.bookSourceComment))
	r=java.post(baseUrl,key,{"referer":String(curl),"cookie":concat(Cookie),"X-Requested-With":"XmlHttpRequest"})
	split(concat(r.cookies()))
	return concat(Cookie)
}
c=updateCookie()
html=java.post(baseUrl,'',{"referer":String(curl),"cookie":c,"Content-type":"application/x-www-form-urlencoded"}).body()
//java.log(html)
java.setContent(html)
result=String(java.getString('data')).replace(/<p><span .+?<\/span><\/p>/,'').replace(/<div class=\s*.ad[\s\S]+?<\/div>/g,'')
result.replace(/ *<i .+?<\/i>/g,function(a){m=a.match(/h=.*?t='(.*?)'/);return m?m[1]:''})

ruleToc.nextTocUrl:
@js:// 目录下一页规则
try {
  // 匹配第1/(\d+)关键词得到目录总页数
  ys=java.getString("#zjlb > div > span:nth-child(2)@text");
  var n = String(ys).match(/1[^\d]+(\d+)/)[1];
  java.log(n);
  var list = [];
  if (n == 1) {
    list;
  } else {
    for (var i = 2; i <= n; i++) {
      var turl = baseUrl.replace(/_1\//, "_");
      list.push(turl + i + "/");
    }
  }
  list;
} catch (e) {}

